const ContentItem = ({ item, onDelete }) => {
  const { canUpdate, canDelete } = usePermissions();
  const [showConfirm, setShowConfirm] = useState(false);
  const [deleteError, setDeleteError] = useState(null);
  
  const subject = { authorId: item.author._id };
  const canEdit = canUpdate(subject);
  const canRemove = canDelete(subject);

  const requestDelete = () => {
    setDeleteError(null);
    setShowConfirm(true);
  };

  const cancelDelete = () => {
    setShowConfirm(false);
  };

  const confirmDelete = async () => {
    try {
      await apiClient.delete(`/content/${item._id}`);
      onDelete();
      setShowConfirm(false);
    } catch (err) {
      setDeleteError('Failed to delete post.');
      console.error(err);
      setShowConfirm(false);
    }
  };

  return (
    <div className="bg-white/90 dark:bg-black/10 p-6 rounded-2xl shadow-lg border border-gray-300 dark:border-white/10 backdrop-blur-lg transition-all duration-300 hover:shadow-xl">
