    e.preventDefault();
    setError(null);
    if (!title || !body) {
      setError('Title and body are required.');
      return;
    }
    try {
      await apiClient.post('/content', { title, body });
      navigate('/');
    } catch (err) {
      setError(err.response?.data?.message || 'Failed to create post.');
      console.error(err);
    }
  };

  return (
    <ContentFormWrapper title="Create New Post">
      <form onSubmit={handleSubmit}>
        {error && <p className="text-red-500 text-center mb-4 text-sm">{error}</p>}
        <div className="mb-4">
          <label className="block text-gray-900 dark:text-gray-200 mb-2 font-medium" htmlFor="title">Title</label>
          <input
            type="text"
            id="title"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            className="w-full px-4 py-2.5 bg-white dark:bg-black/20 border border-gray-400 dark:border-white/10 rounded-lg text-gray-900 dark:text-white placeholder-gray-600 dark:placeholder-gray-400 focus:outline-none focus:ring-2 focus:ring-blue-500 dark:focus:ring-blue-300 transition-all"
          />
        </div>
        <div className="mb-6">
          <label className="block text-gray-900 dark:text-gray-200 mb-2 font-medium" htmlFor="body">Body</label>
          <textarea
            id="body"
            rows="10"
            value={body}
            onChange={(e) => setBody(e.target.value)}
            className="w-full px-4 py-2.5 bg-white dark:bg-black/20 border border-gray-400 dark:border-white/10 rounded-lg text-gray-900 dark:text-white placeholder-gray-600 dark:placeholder-gray-400 focus:outline-none focus:ring-2 focus:ring-blue-500 dark:focus:ring-blue-300 transition-all"
          />
        </div>
        <button
          type="submit"
          className="w-full bg-gradient-to-r from-blue-500 to-blue-600 text-white px-4 py-2.5 rounded-lg shadow-lg shadow-blue-500/30 dark:shadow-blue-400/20 hover:from-blue-600 hover:to-blue-700 transition-all duration-300 transform hover:scale-105"
        >
          Publish Post
        </button>
      </form>
    </ContentFormWrapper>
  );
};

const EditPostPage = () => {
  const [title, setTitle] = useState('');
  const [body, setBody] = useState('');
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();
  const { id } = useParams();

  useEffect(() => {
    const fetchPost = async () => {
      try {
        const { data } = await apiClient.get(`/content/${id}`);
        setTitle(data.title);
        setBody(data.body);
        setLoading(false);
      } catch (err) {
        setError('Failed to fetch post data. You may not have permission to edit this.');
        console.error(err);
        setLoading(false);
      }
    };
    fetchPost();
  }, [id]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError(null);
    if (!title || !body) {
      setError('Title and body are required.');
      return;
    }
    try {
      await apiClient.put(`/content/${id}`, { title, body });
      navigate('/');
    } catch (err) {
      setError(err.response?.data?.message || 'Failed to update post.');
      console.error(err);
    }
  };

  if (loading) {
    return <div className="text-center text-gray-700 dark:text-gray-400">Loading post...</div>;
  }
  
  if (error && !title) {
     return <p className="text-red-500 text-center">{error}</p>;
  }

  return (
    <ContentFormWrapper title="Edit Post">
      <form onSubmit={handleSubmit}>
        {error && <p className="text-red-500 text-center mb-4 text-sm">{error}</p>}
        <div className="mb-4">
          <label className="block text-gray-900 dark:text-gray-200 mb-2 font-medium" htmlFor="title">Title</label>
          <input
            type="text"
            id="title"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            className="w-full px-4 py-2.5 bg-white dark:bg-black/20 border border-gray-400 dark:border-white/10 rounded-lg text-gray-900 dark:text-white placeholder-gray-600 dark:placeholder-gray-400 focus:outline-none focus:ring-2 focus:ring-blue-500 dark:focus:ring-blue-300 transition-all"
          />
        </div>
        <div className="mb-6">
          <label className="block text-gray-900 dark:text-gray-200 mb-2 font-medium" htmlFor="body">Body</label>
          <textarea
            id="body"
            rows="10"
            value={body}
            onChange={(e) => setBody(e.target.value)}
            className="w-full px-4 py-2.5 bg-white dark:bg-black/20 border border-gray-400 dark:border-white/10 rounded-lg text-gray-900 dark:text-white placeholder-gray-600 dark:placeholder-gray-400 focus:outline-none focus:ring-2 focus:ring-blue-500 dark:focus:ring-blue-300 transition-all"
          />
        </div>
        <button
          type="submit"
          className="w-full bg-gradient-to-r from-blue-500 to-blue-600 text-white px-4 py-2.5 rounded-lg shadow-lg shadow-blue-500/30 dark:shadow-blue-400/20 hover:from-blue-600 hover:to-blue-700 transition-all duration-300 transform hover:scale-105"
        >
          Update Post
        </button>
      </form>
    </ContentFormWrapper>
  );
};

const UnauthorizedPage = () => {
  return (
    <div className="text-center p-10 bg-white/90 dark:bg-black/10 rounded-2xl shadow-2xl max-w-lg mx-auto border border-gray-300 dark:border-white/10 backdrop-blur-lg">
      <h1 className="text-5xl font-bold text-transparent bg-clip-text bg-gradient-to-r from-red-500 to-yellow-500 dark:from-red-400 dark:to-yellow-400">Access Denied</h1>
      <p className="mt-6 text-xl text-gray-800 dark:text-gray-300">You do not have the required permissions to view this page.</p>
      <Link to="/" className="mt-8 inline-block bg-gradient-to-r from-blue-500 to-blue-600 text-white px-6 py-3 rounded-lg shadow-md hover:from-blue-600 hover:to-blue-700 transition-all duration-300 transform hover:scale-105">
        Go Back to Home
      </Link>
    </div>
  );
};

const ProtectedRoute = ({ allowedRoles }) => {
  const { user } = useAuth();
  const location = useLocation();
  const { userRole } = usePermissions();

  if (!user) {
    return <Navigate to="/login" state={{ from: location }} replace />;
  }

  if (allowedRoles && !allowedRoles.includes(userRole)) {
    return <Navigate to="/unauthorized" state={{ from: location }} replace />;
  }

  return <Outlet />;
};

export default function App() {
  return (
    <ThemeProvider>
      <GlobalStyles />
      <AuthProvider>
        <BrowserRouter>
          <Routes>
            <Route path="/" element={<Layout />}>
              
              <Route path="/login" element={<LoginPage />} />
              <Route path="/register" element={<RegisterPage />} />
              <Route path="/unauthorized" element={<UnauthorizedPage />} />
              
              <Route element={<ProtectedRoute />}>
                <Route index element={<HomePage />} />
              </Route>
              
              <Route element={<ProtectedRoute allowedRoles={[ROLES.Admin, ROLES.Editor]} />}>
                <Route path="create" element={<CreatePostPage />} />
                <Route path="edit/:id" element={<EditPostPage />} />
              </Route>
              
              <Route element={<ProtectedRoute allowedRoles={[ROLES.Admin]} />}>
                <Route path="admin" element={<AdminPage />} />
              </Route>

            </Route>
          </Routes>
        </BrowserRouter>
      </AuthProvider>
    </ThemeProvider>
  );
}
